mapper.select:
SELECT t_market.*,
t_app_user.real_name userName,
t_school.school_name schoolName
FROM t_market
LEFT JOIN t_app_user ON t_app_user.id=t_market.user_id
LEFT JOIN t_school ON t_school.id=t_market.school_id
WHERE t_market.school_id IN (
	SELECT t_role_school.school_id FROM t_role_school 
	WHERE t_role_school.role_id = #{roleId}
)

	AND (${conds})


	ORDER BY ${os}

LIMIT #{cur},#{len}

SELECT t_market.*,
t_app_user.real_name userName,
t_school.school_name schoolName
FROM t_market
LEFT JOIN t_app_user ON t_app_user.id=t_market.user_id
LEFT JOIN t_school ON t_school.id=t_market.school_id

	1=1

	 	AND t_market.school_id=#{schoolId}


	 	AND t_market.user_id=#{userId}



	LIMIT #{start},#{len}
